let state;

    let plus = document.getElementById("plus");
    plus.addEventListener("click",()=>{
        let hideDiv = document.getElementById("hidediv");
        console.log(hideDiv);
        if(state){
        hideDiv.style.display='none';
        state=false;
        }
        else{
            hideDiv.style.display='block';
            state = true;
        }
    })



